import React, { Component } from 'react';
import ReactDOM from 'react-dom';
import './form.css';

class Form extends Component {
    state = {
      username: "",
      password: "",
      buttonClicked: this.props.isClicked
    };

    componentDidUpdate = (prevProps, prevState) => {
      if (prevState.buttonClicked != this.state.buttonClicked) {
        this.props.getUserInfo(this.state.username, this.state.password)
      }
    }

    setFields = () => {
      this.setState({username: this._un.value, password: this._ps.value})
    }

    changeUsernameHandler = (event) => {
      this.setState({username: event.target.value})
    }
    changePasswordHandler = (event) => {
      this.setState({password: event.target.value})
    }

    render() {
        return (
          <div>
            <div>
              <input ref={(el) => this._un = el} type="text" value={this.state.username}
              onChange={this.changeUsernameHandler} placeholder="Username/Email address">
              </input>
            </div>

            <div>

              <input ref={(el) => this._ps = el} type="text" value={this.state.password}
              onChange={this.changePasswordHandler} placeholder="Password">
              </input>
            </div>
          </div>
        );
    }
}

export default Form;
