import React, { Component } from 'react';
import ReactDOM from 'react-dom';

class LoginButton extends Component {
    state = {
      buttonStyle: {backgroundColor: 'blue', height: '50px', width: '120px',
                    position: 'relative', borderRadius: '5px', },
      textStyle: {fontFamily: 'Georgia', color: 'white', fontSize: '20px'}
    };
    render() {
        return (
            <button style={this.state.buttonStyle} onClick={this.hoverColor}>
              <span style={this.state.textStyle}> Log in </span>
            </button>
        );
    }

    hoverColor = () => {
      this.setState({backgroundColor: 'red'});
    }

}

export default LoginButton;
